//get the reference to the tabs
var ordersTab = document.getElementById("ordersTab");
var productsTab = document.getElementById("productsTab");
var subscribersTab = document.getElementById("subscribersTab");

var currentVisibleTab;
var currentVisibleTabButton;

//funnction for showing tab
function showTab(tab, tabButton) {
    //remove current visible tab
    if (currentVisibleTab) {
        currentVisibleTab.style.display = "none";
        currentVisibleTabButton.classList.remove("tabButtonActive")
    }
    tab.style.display = "table";
    currentVisibleTab = tab;
    //Now make the tabButton glow
    tabButton.classList.add("tabButtonActive");
    currentVisibleTabButton = tabButton;
}

//onclick listeners for tab buttons
ordersTabButton.onclick = () => {
    showTab(ordersTab, ordersTabButton);
}

productsTabButton.onclick = () => {
    showTab(productsTab, productsTabButton);
}

subscribersTabButton.onclick = () => {
    showTab(subscribersTab, subscribersTabButton);
}

